/*
    you want to look at right and down
    the distance you can traval to the right is Math.min(matrix[row][col + 1].right + 1, maxtrix[row+1][col].right)
    the distance you can travel to down is Math.min(matrix[row][col+1].down, matrix[row+1][col].down + 1)
    if these two distance are the same, there is a square at the current position (the current position is the upper left cornor of the square)
    you need another variable max to track the maximum matrix you can have
*/
var maximalSquare = function(matrix) {
  var max = 0, row, col,
  maxRow = matrix.length,
  maxCol = matrix[0] && matrix[0].length || 0;

  for (row = maxRow - 1; row >= 0; row--) {
    for (col = maxCol - 1; col >= 0; col--) {
      matrix[row][col] = {
        val: matrix[row][col],
        right: 0,
        down: 0,
        squareLength: 0
      };

      let cur = matrix[row][col];
      if (cur.val === '1') {
        cur.right = 1;
        cur.down = 1;
        cur.squareLength = 1;

        let rightCell = col + 1 < maxCol && matrix[row][col + 1],
        downCell = row + 1 < maxRow && matrix[row+1][col],
        diagCell = row + 1 < maxRow && col + 1 < maxCol && matrix[row+1][col+1];

        cur.right += rightCell && rightCell.right || 0;
        cur.down += downCell && downCell.down || 0;
        if (((rightCell && rightCell.right || 0) >= diagCell && diagCell.squareLength || 0) &&
        ((downCell && downCell.down || 0) >= diagCell && diagCell.squareLength || 0)) {
          cur.squareLength = diagCell.squareLength + 1;
        } else {
          // see if you can form a new square if you can't extend from the diagonal one
          cur.squareLength = Math.max(
            Math.min(
              Math.min(rightCell && rightCell.right || 0, diagCell && diagCell.squareLength || 0),
              Math.min(downCell && downCell.down || 0, diagCell && diagCell.squareLength || 0)) + 1,
            1);
        }

        max = Math.max(max, cur.squareLength * cur.squareLength);
      }
    }
  }

  return max;
};

